# concatenation
text = "concatenate"
print(text[5])

# age interpolation
age = 25
print(f"I am {age} years old")

#concatenate city
city = "New"
space = " "
country = "York"
print(city + space + country)

#string alphabet
alphabet = "ABCDEFGHIJKLMNOPQRSTUVWXYZ"
